import {useState} from 'react'
import useInput from './../hooks/use-input'
const SimpleInput = (props) => {
    const {
        value: enteredValue,
        hasError: inputHasError,
        isValid: enteredInputIsValid,
        valueChangeHandler: inputChangedHandler,
        valueBlurHandler: inputBlurHandler,
        reset

    } = useInput(value => value.trim() !== '')
    const [enteredName, setEnteredName] = useState('')
    const [enteredEmail, setEnteredEmail] = useState('')

    const [inputFieldTouched, setInputFieldTouched] = useState(false)
    const [emailFieldTouched, setEmailFieldTouched] = useState(false)

    const enteredNameIsValid = enteredName.trim() !== ''
    const enteredEmailIsValid = enteredName.includes('@')

    const nameInputIsInvalid = inputFieldTouched && !enteredNameIsValid
    const emailInputIsInvalid = emailFieldTouched && !enteredEmailIsValid

    let formIsValid = false;
    if(enteredNameIsValid && enteredEmailIsValid) {
        formIsValid = true
    }

    const nameInputChangeHandler = event => {
        setEnteredName(event.target.value);
    }

    const emailInputChangeHandler = event => {
        setEnteredEmail(event.target.value);
    }

    const nameInputBlurHandler = event => {
        setInputFieldTouched(true)
        if(!enteredNameIsValid) {
            return
        }
    }
    const emailInputBlurHandler = event => {
        setEmailFieldTouched(true)
        if(!enteredEmailIsValid) {
            return
        }
    }

    const formSubmissionHandler = event => {
        event.preventDefault()
        setInputFieldTouched(true)
        setEmailFieldTouched(true)
        if(!enteredNameIsValid || !enteredEmailIsValid) {
            return;
        }
        setEnteredName('')
        setEnteredEmail('')
        setInputFieldTouched(false)
        setEmailFieldTouched(false)
    }

    const nameInputClasses = !nameInputIsInvalid ? 'form-control' : 'form-control invalid '
    const emailInputClasses = !nameInputIsInvalid ? 'form-control' : 'form-control invalid '
  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={inputChangedHandler} onBlur={inputBlurHandler} value={enteredName}/>
          {enteredInputIsValid && <p className={'input'}>Invalid input. Entered name must not be empty.</p>}
      </div >
        <div className={emailInputClasses}>
            <label htmlFor='email'>Your Email</label>
            <input type='email' id='email' onChange={emailInputChangeHandler} onBlur={emailInputBlurHandler} value={enteredEmail}/>
            {emailInputIsInvalid && <p className={'input'}>Invalid input. Entered email must not be empty.</p>}
        </div>
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
