import {createSlice} from '@reduxjs/toolkit';

const initialCartState = { counter: 0 }
const cart = createSlice({
    name: cart,
    initialState: initialCartState,
    reducer: {
        increment(state) {
            state.counter++
        },
        decrement(state) {
            state.counter--
        }
    }
})
export default cart.reducer;
